package com.lyrne.backend.services;

import com.lyrne.backend.Main;
import com.lyrne.backend.User;
import org.joda.time.DateTime;
import org.joda.time.Interval;
import org.joda.time.LocalDate;
import org.joda.time.LocalTime;

import java.util.*;

public class FakeUsers {

    private static final Random random = new Random();

    private static final String image1 = "data:image/png;base64,";

    private static Main.Subject randomSubject() {
        return Main.Subject.values()[random.nextInt(Main.Subject.values().length)];
    }

    private static List<Interval> randomIntervals() {
        List<Interval> intervals = new ArrayList<>();
        Random random = new Random();
        Set<Integer> usedDays = new HashSet<>();

        while (usedDays.size() < 7) {
            int dayOfWeek = random.nextInt(7) + 1; // 1 = Monday, 7 = Sunday
            if (usedDays.contains(dayOfWeek)) continue;

            usedDays.add(dayOfWeek);
            LocalDate date = new LocalDate(2024, 1, 1).withDayOfWeek(dayOfWeek); // Arbitrary reference week
            LocalTime startTime = new LocalTime(random.nextInt(24), random.nextInt(60));
            LocalTime endTime = startTime.plusHours(random.nextInt(1, 4)); // Random duration of 1-3 hours

            DateTime startDateTime = date.toDateTime(startTime);
            DateTime endDateTime = date.toDateTime(endTime);

            intervals.add(new Interval(startDateTime, endDateTime));
        }
        return intervals;
    }

    public static void create(int amount) {
        for (int i = 0; i < amount; i++) {
            User user = new User("FakeUser_" + i);
            user.setUsername("fakeuser" + i);
            user.setEmail("fakeuser" + i + "@email.com");
            user.setRole(random.nextBoolean() ? User.Role.TUTOR : User.Role.STUDENT);
            user.setIcon(image1);
            user.setPhone("780 473 7373");
            user.getSubjects().add(randomSubject());
            user.getAvailability().addAll(randomIntervals());
            user.setNew(false);

            DatabaseManager.saveUser(user);
        }
    }
}
